sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   //Generate an alert when the form loads
	alert("Thank you for loading the Needit form. When you close this alert you will be able to interact with the form.");
}

function onSubmit(){
	//generate an alert to thank the user for submitting a redord
	alert("Thank you for submitting a NeddIt request.");
}

function onChange(control, oldValue, newValue, isLoading, isTemplate){
	if (isLoading || newValue ==='' || isTemplate){
		return;
	}

	//Generate an alert showing the oldValue and newValue
	alert("You have changed the Short description from " + oldValue + " to " + newValue + ".");
}
